<?php
/**
 * PhotoShelter Integration
 * Do not edit this file unless you know what you're doing
 */

if ( ! function_exists( 'gpp_photoshelter_meta' ) ) :

    function gpp_photoshelter_meta(){

        $theme = wp_get_theme();

        // Get user selected alt stylesheet
        //$theme_options = get_option( 'gpp_base_options' );
		$theme_options = get_option( gpp_get_current_theme_id() . '_options');
		$css = $theme_options['color'];

        if ( isset( $css) && '' != $css ) {
          $style = get_stylesheet_directory_uri() . '/css/' . $css . '.css';
        } else {
          $style = get_stylesheet_directory_uri() . '/css/default.css';
        } 

        echo '<meta name="ps_configurator" content="thmNm=' . $theme['Name'] . ';thmVsn=' . $theme['Version'] . ';thmFmly=' . $theme['Template'] . ';hd_bgn=BeginHeader;hd_end=EndHeader;ft_bgn=BeginFooter;ft_end=EndFooter;scptInc=https://ajax.googleapis.com/ajax/libs/jquery/1.7.2/jquery.min.js;scptInc=//html5shiv.googlecode.com/svn/trunk/html5.js;scptInc=' . get_template_directory_uri() . '/library/js/css3-mediaqueries.js;scptInc=' . get_template_directory_uri() . '/library/js/jquery.fitvids.js;lnkInc=' . $style . ';lnkInc=' . get_bloginfo('stylesheet_url') . ';lnkInc=' . get_template_directory_uri() . '/library/includes/responsive.css" />',"\n";

    }
    add_action( 'wp_head', 'gpp_photoshelter_meta' );

endif;

?>